  157: { spa: 'SE_SpellDamageShield', effectName: 'Spell Damage Shield', description: 'Inflicts non-melee damage on caster of a spell.', category: 'Damage Shield', base: 'amount damage shield (negative)', limit: 'none', value: 'unknown', notes: 'Spells must have \'feedbackable\' field set to a value otherwise they will not be affected by spell damage shields.'},
