  203: { spa: 'SE_MassGroupBuff', effectName: 'Mass Group Buff', description: 'Allows next casted Group Buff to hit all players and pets within a large radius from caster at double the mana cost.', category: 'Beneficial Uncategorized', base: '1', limit: 'none', value: 'none', notes: ''},
  204: { spa: 'SE_GroupFearImmunity', effectName: 'Group Fear Immunity', description: 'Provides immunity to fear for group. ', category: 'Resist', base: 'duration', limit: 'none', value: 'none', notes: 'Duration is calculated as base value * 10. Thus, value of 1 would be 10 seconds. This is not a buff and gives no icon.'},
  205: { spa: 'SE_Rampage', effectName: 'AE Rampage', description: 'Perform a primary slot combat rounds on all creatures within a 40 foot radius.', category: 'Instant Combat', base: 'number of attack rounds', limit: 'max entities hit per round', value: 'aoe range override', notes: 'On live base is always set to 1, if more than one attack is a spell it uses this SPA in multiple slots. Limit value can be used to set a max amount of entities able to be attacked per round.'},
  206: { spa: 'SE_AETaunt', effectName: 'AE Taunt', description: 'Taunts all creatures within a 40 foot radius, placing you \'base values\' points of hate higher than your opponents\' previously most hated target.', category: 'Aggro', base: 'added hate', limit: 'none', value: 'aoe range override', notes: ''},
