  210: { spa: 'SE_PetShield', effectName: 'Pet Shield', description: 'Allows pet to use \'shield ability\' on owner to reduce 50 percent of damage taken by owner for duration', category: 'Pet', base: 'Duration multiplier 1=12 seconds, 2=24 ect', limit: 'mitigation on pet owner override', value: 'mitigation on pet overide', notes: 'Special: limit and max values are not on live, they can be used to give mitigation penalties or bonuses to shielder or shielded.'},
