  212: { spa: 'SE_FrenziedDevastation', effectName: 'Frenzied Devastation', description: 'Increase spell critical chance and while present all direct damage spells cost double the amount of mana.', category: 'Spell Bonus', base: '1', limit: 'chance modifier', value: 'none', notes: 'Live no longer uses the effect in this way. It is now a focus effect.'},
  213: { spa: 'SE_PetMaxHP', effectName: 'Pet Max HP', description: 'Modifies your pets maximum HP by percent.', category: 'Pet Bonuses From Owner', base: 'percent modifier', limit: 'none', value: 'none', notes: 'This effect goes on the pet owner and then the benefit is applied to the pet.'},
