  439: { spa: 'SE_Assassinate', effectName: 'Assassinate', description: 'Grants  backstab and critical throwing attacks a chance to deal massive damage to targets when attacking from behind.', category: 'Fatal Procs', base: 'percent chance', limit: 'damage amount', value: 'none', notes: 'Use with SPA 345 to set max target level and bonus chance.'},
  440: { spa: 'SE_FinishingBlowLvl', effectName: 'Finishing Blow Max Level', description: 'Sets the max level and max hit point ratio an NPC must be to receive a Finishing Blow.', category: 'Fatal Procs', base: 'max target level', limit: 'hit point percent to trigger', value: 'none', notes: 'Limit value is calculated as limit / 10. To set FB to trigger below 10 pct HP, set limit to 100. If multiple version of this affect exist it will use highest HP percent.'},
  441: { spa: 'SE_DistanceRemoval', effectName: 'Distance Buff Removal', description: 'Buff is removed from target when target moves specified amount of distance away from where initially hit.', category: 'Utility Uncategorized', base: 'distance amount', limit: 'none', value: 'none', notes: ''},
  442: { spa: 'SE_TriggerOnReqTarget', effectName: 'Trigger Spell on Target Requirement', description: 'Cast a spell when Target Requirement conditions are met.', category: 'Cast On Effect', base: 'spellid', limit: 'Target Restriction ID', value: 'none', notes: 'See enum SpellRestriction in spdat.h for IDs. This trigger spell is usually cast on a target.'},
